SELECT DISTINCT 
    PROCESS.SEQUENCE_ID
FROM META_RAW.USECASE
LEFT JOIN META_RAW.PROCESS USING(USECASE_ID)
LEFT JOIN META_RAW.PHASE USING(PHASE_ID)
WHERE USECASE.USECASE_ID = @{pipeline().parameters.usecase_id}
    AND PHASE_ID = @{pipeline().parameters.phase_id}
    AND USECASE.IS_ACTIVE = TRUE
    AND PROCESS.IS_ACTIVE = TRUE
ORDER BY PROCESS.SEQUENCE_ID ASC;

SELECT DISTINCT 
    PROCESS.SEQUENCE_ID
FROM META_RAW.USECASE
LEFT JOIN META_RAW.PROCESS USING(USECASE_ID)
LEFT JOIN META_RAW.PHASE USING(PHASE_ID)
WHERE USECASE.USECASE_ID = @{pipeline().parameters.usecase_id}
    AND PHASE_ID = @{pipeline().parameters.phase_id}
    AND USECASE.IS_ACTIVE = TRUE
    AND PROCESS.IS_ACTIVE = TRUE
ORDER BY PROCESS.SEQUENCE_ID ASC;

SELECT DISTINCT 
    PROCESS.SEQUENCE_ID
FROM META_RAW.USECASE
LEFT JOIN META_RAW.PROCESS USING(USECASE_ID)
LEFT JOIN META_RAW.PHASE USING(PHASE_ID)
WHERE USECASE.USECASE_ID = @{pipeline().parameters.usecase_id}
    AND PHASE_ID = @{pipeline().parameters.phase_id}
    AND USECASE.IS_ACTIVE = TRUE
    AND PROCESS.IS_ACTIVE = TRUE
ORDER BY PROCESS.SEQUENCE_ID ASC;

SELECT *
FROM META_RAW.PROCESS
JOIN META_RAW.SOURCE USING(SOURCE_ID)
WHERE PROCESS.USECASE_ID = @{pipeline().parameters.usecase_id}
AND PROCESS.PHASE_ID = @{pipeline().parameters.phase_id}
AND PROCESS.SEQUENCE_ID = @{pipeline().parameters.sequence_id}
AND PROCESS.IS_ACTIVE = TRUE
ORDER BY PROCESS.SEQUENCE_ID ASC;

