COPY INTO @{concat('@UTILITY_RAW.INTSTG_USERERRORNOTIFICATION/',pipeline().parameters.parameters.value[0].container,'/',item().model_name,'/',item().model_name,'_',formatDateTime(convertTimeZone(variables('currentTime'), 'UTC', 'Singapore Standard Time'),'yyyyMMdd_HHmmss'),'.csv')} 
FROM (
  @{replace(activity('Script3').output.resultSets[0].rows[0].QUERY,';','')}
)
FILE_FORMAT = (
  TYPE = 'CSV'
  FIELD_DELIMITER = ','
  ENCODING = 'UTF-8',
  COMPRESSION = 'NONE'
  NULL_IF =() 
  EMPTY_FIELD_AS_NULL = FALSE
) 
single = true 
header = true 
max_file_size = 4900000000;

